import React, { useState } from 'react'
import { ThemeProvider as SCThemeProvider } from 'styled-components'
import {
  light as lightBase,
  Menu as menuBase,
  dark as darkBase,
  PancakeTheme,
  SvgProps as svgProps,
} from '@pancakeswap-libs/uikit'

// customize light theme
const light: PancakeTheme = lightBase
light.card.background = '#fff000'

// customize dark theme
const dark: PancakeTheme = darkBase
dark.card.background = '#fff000'
dark.nav.hover = '#131313'
dark.colors.text = '#fff000'
dark.card.cardHeaderBackground = {
  default: 'yellow',
  violet: 'yellow',
  blue: 'yellow',
}

const CACHE_KEY = 'IS_DARK'

export interface ThemeContextType {
  isDark: boolean
  toggleTheme: () => void
}

const ThemeContext = React.createContext<ThemeContextType>({ isDark: false, toggleTheme: () => null })

const ThemeContextProvider: React.FC = ({ children }) => {
  const [isDark, setIsDark] = useState(() => {
    const isDarkUserSetting = localStorage.getItem(CACHE_KEY)
    return isDarkUserSetting ? JSON.parse(isDarkUserSetting) : false
  })

  const toggleTheme = () => {
    setIsDark((prevState: any) => {
      localStorage.setItem(CACHE_KEY, JSON.stringify(!prevState))
      return !prevState
    })
  }

  return (
    <ThemeContext.Provider value={{ isDark, toggleTheme }}>
      <SCThemeProvider theme={isDark ? dark : light}>{children}</SCThemeProvider>
    </ThemeContext.Provider>
  )
}

export { ThemeContext, ThemeContextProvider }
